    useEffect ( () => {
        fetchItems(); 
    }, [])
    const[email, setEmail] = useState('')
    const[body, setBody] = useState('')
    const[password, setPassword] = useState('')
    const[loginStatus, setLoginStatus] = useState(false)


    const navigate= useNavigate();


    const fetchItems = async() => {
        console.log("hej"); 
        const data = await fetch('/LogIn'); 
        const hashPass = await data.json(); 
        console.log(hashPass); 
    }

        // Functionality after pushing submit
      const handleSubmit = (e) =>{
            e.preventDefault();

            // Redirecting to homepage after click



            const myData = {email, password}

            // Sending userdata through a POST request to server
            fetch('http://localhost:3000/LogIn',{
                method: 'POST',
                headers: {
                    'Content-Type':'application/json'
                },
                body: JSON.stringify(myData),
                // console.log(response)
            })
            .then(response => response.json())
            .then(data => {
                console.log('authentification: ' + data.auth)
                if(data.auth){
                    console.log(data.auth)
                    setLoginStatus(true)
                    navigate('/')
                    alert('You are now Logged in!')
                    console.log("User logged in"); 

                }else if (!data.auth){
                    console.log(data.auth)
                    alert('Wrong password try again.')
                    console.log('log in failed')
                    setLoginStatus(false)
                }
            })

                // console.log(myData)
                // fetchItems(); 

        }

return(
